/* styles/AdminDashboard.module.css */
.dashboardLayout {
  --bg-color: #F8F4E3;
  --primary-color: #3d3d3d;
  --gold-accent: #DAA520;
  --card-bg: #FFFFFF;
  --border-color: #EAE0C8;

  width: 100vw;
  min-height: 100vh;
  background-color: var(--bg-color);
  font-family: 'Georgia', serif;
  padding: 40px;
}

.header {
  text-align: center;
  margin-bottom: 50px;
}

.header h1 {
  margin: 0;
  font-size: 3rem;
  font-weight: normal;
  color: var(--primary-color);
}

.header p {
  font-size: 1.1rem;
  color: #777;
  margin-top: 10px;
}

/* Grid for the navigation cards */
.navGrid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 30px;
  max-width: 900px;
  margin: 0 auto;
}

/* Individual Card Styles */
.navCard {
  background-color: var(--card-bg);
  border-radius: 15px;
  padding: 25px;
  text-align: center;
  box-shadow: 0 5px 20px rgba(0,0,0,0.07);
  border: 1px solid var(--border-color);
  text-decoration: none;
  color: var(--primary-color);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  opacity: 0; /* Initially hidden for animation */
}

/* Staggered slide-in animation for cards */
.navCard:nth-child(1) { animation: slideInUp 0.5s 0.1s ease-out forwards; }
.navCard:nth-child(2) { animation: slideInUp 0.5s 0.2s ease-out forwards; }
.navCard:nth-child(3) { animation: slideInUp 0.5s 0.3s ease-out forwards; }
.navCard:nth-child(4) { animation: slideInUp 0.5s 0.4s ease-out forwards; }

@keyframes slideInUp {
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.navCard:hover {
  transform: translateY(-8px);
  box-shadow: 0 10px 25px rgba(0,0,0,0.1);
}

.cardIcon {
  font-size: 2.5rem;
  color: var(--gold-accent);
  margin-bottom: 15px;
}

.cardTitle {
  font-size: 1.3rem;
  font-weight: bold;
  margin: 0;
}

/* Logout Button at the bottom */
.logoutButtonContainer {
  text-align: center;
  margin-top: 50px;
}

.logoutButton {
  padding: 12px 30px;
  font-size: 1rem;
  color: var(--gold-accent);
  background-color: transparent;
  border: 1px solid var(--gold-accent);
  border-radius: 25px;
  cursor: pointer;
  transition: all 0.3s ease;
}

.logoutButton:hover {
  background-color: var(--gold-accent);
  color: white;
}

.loading {
  /* ... existing styles ... */
}

styles/Dashboard.module.css.content:
/* styles/Dashboard.module.css */

.dashboardLayout {
  --primary-dark: #3D52A0;
  --primary-medium: #7091E6;
  --primary-light: #8697C4;
  --secondary-light: #ADBBDA;
  --background-color: #EDE8F5;
  --text-color-dark: #2c3e50;
  --text-color-light: #ffffff;

  display: flex;
  width: 100vw;
  height: 100vh;
  position: relative;
  overflow: hidden;
  background: linear-gradient(160deg, var(--background-color) 0%, var(--secondary-light) 100%);
}

.mainContent {
  flex-grow: 1;
  overflow-y: auto;
  transition: filter 0.4s ease-in-out;
}

.mainContent.blurred {
  filter: blur(5px) brightness(0.9);
}

/* --- New Header Styles --- */
.header {
  padding: 20px 40px;
}
.headerTitle {
  display: flex;
  align-items: center;
  gap: 20px;
}
.headerTitle h1 {
  margin: 0;
  font-size: 2.5rem;
  color: var(--primary-dark);
}

.welcomeMessage {
  background-color: rgba(255, 255, 255, 0.7);
  padding: 25px;
  border-radius: 12px;
  box-shadow: 0 4px 15px rgba(0,0,0,0.05);
  font-size: 1.1rem;
  color: var(--text-color-dark);
  margin: 0 40px;
}

/* --- Sidebar Styles --- */
.sidebar {
  position: fixed;
  top: 0;
  left: 0;
  height: 100%;
  width: 280px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(25px);
  -webkit-backdrop-filter: blur(25px);
  border-right: 1px solid rgba(255, 255, 255, 0.2);
  display: flex;
  flex-direction: column;
  padding: 25px;
  transform: translateX(-100%);
  transition: transform 0.4s cubic-bezier(0.25, 0.8, 0.25, 1);
  z-index: 100;
}

.sidebar.open {
  transform: translateX(0);
}

/* Find and update .sidebarHeader */
.sidebarHeader {
  display: flex;
  justify-content: space-between;
  align-items: center;
  text-align: left;
  margin-bottom: 40px;
}
.sidebarHeader h2 {
  margin: 0;
  color: var(--primary-dark);
  font-size: 1.8rem;
}

/* Add this new style for the close button */
.closeButton {
  background: transparent;
  border: none;
  color: var(--primary-dark);
  font-size: 28px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 5px;
  border-radius: 50%;
  transition: background-color 0.2s, color 0.2s;
}
.closeButton:hover {
  background-color: rgba(0,0,0,0.1);
}

.sidebarNav ul { list-style-type: none; padding: 0; margin: 0; }
.sidebarNav li { opacity: 0; transform: translateX(-30px); }
.sidebar.open .sidebarNav li { animation: slideAndFadeIn 0.5s ease-out forwards; }
.sidebar.open .sidebarNav li:nth-child(1) { animation-delay: 0.1s; }
.sidebar.open .sidebarNav li:nth-child(2) { animation-delay: 0.2s; }
.sidebar.open .sidebarNav li:nth-child(3) { animation-delay: 0.3s; }
.sidebar.open .sidebarNav li:nth-child(4) { animation-delay: 0.4s; }

@keyframes slideAndFadeIn {
  from { opacity: 0; transform: translateX(-30px); }
  to { opacity: 1; transform: translateX(0); }
}

.sidebarNav li a {
  display: flex;
  align-items: center;
  gap: 15px;
  padding: 15px;
  color: var(--primary-dark);
  text-decoration: none;
  border-radius: 10px;
  margin-bottom: 10px;
  font-weight: 500;
  background-color: rgba(255, 255, 255, 0.4);
  transition: background-color 0.3s, transform 0.2s, box-shadow 0.3s;
}
.sidebarNav li a:hover {
  background-color: rgba(255, 255, 255, 0.8);
  transform: translateX(5px);
  box-shadow: 0 4px 10px rgba(0,0,0,0.1);
}
.sidebarNav li a.active {
  background-color: var(--primary-dark);
  color: var(--text-color-light);
  font-weight: bold;
}

.logoutButton {
  margin-top: auto;
  display: flex;
  align-items: center;
  gap: 15px;
  padding: 15px;
  font-size: 1rem;
  font-weight: 500;
  color: var(--primary-dark);
  background-color: rgba(255, 255, 255, 0.4);
  border: none;
  border-radius: 10px;
  cursor: pointer;
